<!doctype html>
<html lang="en">

<head>
    <!-- Required meta tags -->
    <meta charset="utf-8">
    <meta name="viewport" content="width=device-width, initial-scale=1">

    <!-- Bootstrap CSS -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/jquery/3.6.0/jquery.js"
        integrity="sha256-H+K7U5CnXl1h5ywQfKtSj8PCmoN9aaq30gDh27Xc0jk=" crossorigin="anonymous"></script>
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.0.2/dist/css/bootstrap.min.css" rel="stylesheet"
        integrity="sha384-EVSTQN3/azprG1Anm3QDgpJLIm9Nao0Yz1ztcQTwFspd3yD65VohhpuuCOmLASjC" crossorigin="anonymous">
    <title>Hello, world!</title>
</head>
<style>
</style>

<body>
    <div class="container bg-secondary ">
            <img src="data:image/jpeg;base64,"
                class="img-fluid container" alt="..." width="500px">
        <div class="container mt-5 ">
            <div style="text-align: center; width: 200px; height: 1270px; float: left;" class="col-12 bg-danger">Float:left</div>
        </div>
        <div class="row mt-5 ">
            <div class="col-4">
                <div class="card" style="width: 18rem;">
                    <img src="https://static.amarintv.com/images/upload/editor/source/news/1-1594264049719.jpg"
                        class="card-img-top" alt="...">
                    <div class="card-body">
                        <p class="card-text">ปัจจุบันมีหลักฐานมากมาย ที่มีหลายๆกลุ่มคนพยายามออกมาแสดงข้อมูล หลักฐาน ว่า UFO มีอยู่จริง</p>
                    </div>
                </div>
            </div>
            <div class="col-4">
                <div class="card" style="width: 18rem;">
                    <img src="https://www.setiusa.net/wp-content/uploads/2016/03/ufo-pic.jpg"
                        class="card-img-top" alt="...">
                    <div class="card-body">
                        <p class="card-text">UFO เป็นเพียงยาน ของสิ่งมีชีวิต ที่คล้ายกับมนุษย์ โดนจะถูกเรียกว่ามนุษย์ต่างดาวนั่นเอง</p>
                    </div>
                </div>
            </div>
            
            <button id="B_Back" class="bg-success " > back </button>
            <div class="container">
                <div id="Main">
                    <table class="table table-dark">
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>Title</th>
                                <th>Author</th>
                                <th>comment</th>
                            </tr>
                        </thead>
                        <tbody id="TB_Post">
                        </tbody>
                    </table>
                </div>
                <div id="Detail">
                    <table class="table table-dark">
                        <thead id="TBL_Details_1">
                        </thead>
                        <tbody id="TBL_Details">
                        </tbody>
                    </table>
                </div>
                <div id="Comment">
                    <table class="table table-dark">
                        <thead id="TBL_Comment_1">
                        </thead>
                        <tbody id="TBL_Comment">
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
        <div class="row bg-primary">
            <div style="text-align:center;" class="col-12 ">
                <h1>จัดทำโดย</h1>
                <p>63102156</p>
                <p>นายชัยณรงค์ แก้วประเสริฐ</p>
            </div>
        </div>
    </div>
</body>
<script>
    function showDetails(id) {
        $("#Main").hide();
        $("#Detail").show();
        var url = "https://jsonplaceholder.typicode.com/posts/" + id
        $.getJSON(url)
            .done((data) => {
                console.log(data);
                var line_t2 = "<tr id='Detail_ROW'>";
                    line_t2 += "<th><b> ID </b></th>"
                    line_t2 += "<th><b> Title </b></th>"
                    line_t2 += "<th><b> UserId </b></th>"
                    line_t2 += "</tr>";
                    $("#TBL_Details_1").append(line_t2);
                var line_t1 = "<tr id='Detail_ROW_1'";
                    line_t1 += "><td>" + data.id + "</td>"
                    line_t1 += "<td><b>" + data.title + "</b><br/>"
                    line_t1 += data.body + "</td>"
                    line_t1 += "<td>" + data.userId + "</td>"
                    line_t1 += "</tr>";
                    $("#TBL_Details").append(line_t1);
            })
            .fail((xhr, err, status) => {
            })
    }
    function showComment(id) {
        $("#Main").hide();
        $("#Comment").show();
        var url = "https://jsonplaceholder.typicode.com/comments/" + id
        $.getJSON(url)
            .done((data) => {
                console.log(data);                
                var line_c2 = "<tr id='Comment_ROW'>";
                    line_c2 += "<th><b> ID </b></th>"
                    line_c2 += "<th><b> Name </b></b></th>"
                    line_c2 += "<th><b> Email </b></th>"
                    line_c2 += "<th><b> Title </b></th>"
                    line_c2 += "<th><b> PostID </b></th>"
                    line_c2 += "</tr>";
                    $("#TBL_Comment_1").append(line_c2);
                var line_c1 = "<tr id='Comment_ROW_1'";
                    line_c1 += "><td>" + data.id + "</td>"
                    line_c1 += "<td><b>" + data.name+ "</b></td>"
                    line_c1 += "<td>" + data.email + "</td>"
                    line_c1 += "<td>" + data.body + "</td>"
                    line_c1 += "<td>" + data.postId + "</td>"
                    line_c1 += "</tr>";
                    $("#TBL_Comment").append(line_c1);
            })
            .fail((xhr, err, status) => {
            })
    }
    function Posts() {
        var url = "https://jsonplaceholder.typicode.com/posts"
        $.getJSON(url)
            .done((data) => {
                $.each(data, (k, item) => {
                    var line = "<tr>";
                    line += "<td>" + item.id + "</td>"
                    line += "<td><b>" + item.title + "</b><br/>"
                    line += item.body + "</td>"
                    line += "<td><button onClick='showDetails(" + item.id + ");'>Link</button></td>"
                    line += "<td><button onClick='showComment(" + item.id + ");'>Comment</button></td>"
                    line += "</tr>";
                    $("#TB_Post").append(line);
                });
                $("#Main").show();
            })
            .fail((xhr, err, status) => {
            })
    }
    $(() => {
        Posts();
        $("#Detail").hide();
        $("#Comment").hide();
        $("#B_Back").click(() => {
            $("#Main").show();
            $("#Detail").hide();
            $("#Comment").hide();
            $("#Detail_ROW").remove();
            $("#Detail_ROW_1").remove();
            $("#Comment_ROW").remove();
            $("#Comment_ROW_1").remove();
        });
    })
</script>

</html>
